static byte const _music[92] = { Cs1, 12, D1, 40, E1, 4, F1, 4, D1, 40, E1, 4, F1, 4, D1, 16, E1, 4, F1, 4, D1, 16, E1, 4, F1, 4, Fs1, 12, G1, 24, Gs1, 12, A1, 40, Bb1, 4, C2, 4, A1, 40, Bb1, 4, C2, 4, A1, 16, Bb1, 4, C2, 4, A1, 16, Bb1, 4, C2, 4, D2, 12, Fs1, 24, G1, 12, C2, 12, E1, 24, F1, 12, Bb1, 12, Cs1, 24, D1, 12, A1, 11, 0, 1, A1, 12, C2, 17, 0, 1, C2, 6, A1, 24, 0, 3, 0, 0 };

byte* _pieces[8];
byte _map[4][2];
byte _cur_x = 0;
byte _cur_y = 0;

void init_pieces() {
	byte i = 0;
	for (; i < 8; ++i) {
		_pieces[i] = (byte*)malloc(128);
	}
}

void load_puzzle(byte* puzzle) {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte i = 0;
	unsigned int o = 0;
	
	for (y = 0; y < 2; ++y) {
		for (x = 0; x < 4; ++x) {			
			byte* piece = _pieces[i];

			_map[x][y] = i;

			o = ((unsigned int)y * (unsigned int)512) + ((unsigned int)x * (unsigned int)32);

			for (xb = 0; xb < 32; ++xb) {
				piece[xb] = puzzle[o + xb];
				piece[xb + 32] = puzzle[o + xb + 128];
				piece[xb + 64] = puzzle[o + xb + 256];
				piece[xb + 96] = puzzle[o + xb + 384];
			}

			++i;			
		}
	}
}

void randomize() {
	byte x = 0;
	byte y = 0;
	byte hash[8];
	for (x = 0; x < 8; ++x) {
		hash[x] = 0;
	}

	for (y = 0; y < 2; ++y) {
		for (x = 0; x < 4; ++x) {
			while (1) {
				byte r = (byte)rand() % 8;
				if (hash[r] == 0) {
					_map[x][y] = r;
					hash[r] = 1;
					break;
				}
			}
		}
	}
}

void draw_complete() {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte yb = 0;
	byte i = 0;
	
	for (yb = 0; yb < 2; ++yb) {
		x = 0;
		for (xb = 0; xb < 4; ++xb) {
			i = _map[xb][yb];
			if (i == 0) {
				CLEAR_SPRITE(x, y, 32, 32);
			} else {
				DRAW_SPRITE(_pieces[i], x, y, 32, 32, 0, DRAW_NOP);	
			}			
			x += 32;
		}

		y += 32;
	}
}

void move_piece() {
	byte i = 0;
	byte process = 0;
	byte x = 0;
	byte y = 0;

	i = _map[_cur_x][_cur_y];
	if (i == 0) {
		return;
	}

	x = _cur_x * 32;
	y = _cur_y * 32;

	if (_cur_x > 0 && _map[_cur_x - 1][_cur_y] == 0) {
		_map[_cur_x - 1][_cur_y] = i;
		DRAW_SPRITE(_pieces[i], x - 32, y, 32, 32, 0, DRAW_NOP);
		process = 1;
	} else if (_cur_x < 3 && _map[_cur_x + 1][_cur_y] == 0) {
		_map[_cur_x + 1][_cur_y] = i;
		DRAW_SPRITE(_pieces[i], x + 32, y, 32, 32, 0, DRAW_NOP);
		process = 1;
	} else if (_cur_y > 0 && _map[_cur_x][_cur_y - 1] == 0) {
		_map[_cur_x][_cur_y - 1] = i;
		DRAW_SPRITE(_pieces[i], x, y - 32, 32, 32, 0, DRAW_NOP);
		process = 1;
	} else if (_cur_y < 1 && _map[_cur_x][_cur_y + 1] == 0) {
		_map[_cur_x][_cur_y + 1] = i;
		DRAW_SPRITE(_pieces[i], x, y + 32, 32, 32, 0, DRAW_NOP);
		process = 1;
	}

	if (process) {
		_map[_cur_x][_cur_y] = 0;
		CLEAR_SPRITE(x, y, 32, 32);

		i = 0;
		for (y = 0; y < 2; ++y) {
			for (x = 0; x < 4; ++x) {
				if (_map[x][y] != i) {
					return;
				}

				++i;
			}
		}

		// Winner!
		DRAW_SPRITE(_pieces[0], 0, 0, 32, 32, 0, DRAW_NOP);
		DISPLAY();

		for (;;) {
			WAIT();
		}

	}
}

void processButtons(byte buttons) {
	if ((buttons & 1) == 0) {
		if (_cur_y > 0) {
			--_cur_y;
		}
	} if ((buttons & 2) == 0) {
		if (_cur_y < 1) {
			++_cur_y;
		}
	} else if ((buttons & 4) == 0) {
		if (_cur_x > 0) {
			--_cur_x;
		}

	} else if ((buttons & 8) == 0) {
		if (_cur_x < 3) {
			++_cur_x;
		}
	} else if ((buttons & 16) == 0) {
		move_piece();
	}
}

int main() {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte yb = 0;
	byte prevButtons = 0;
	byte buttons = 0;
	byte buttonCounter = 0;
	byte drawCursor = 1;
	byte cursorVis = 0;
	unsigned int frameCount = 0;

	api_init();

	init_pieces();
	load_puzzle((byte*)_image);
	randomize();
	draw_complete();

	LOAD_MUSIC((unsigned char*)_music);
	
	for (;;) {			
		++frameCount;

		x = _cur_x * 32;
		y = _cur_y * 32;

		if (drawCursor) {
			if (cursorVis == 0) {
				cursorVis = 1;
			} else {
				cursorVis = 0;
			}

			DRAW_SPRITE((byte*)_cursor, x, y, 32, 32, 0, DRAW_XOR);
			drawCursor = 0;
		}

		DISPLAY();

		if (frameCount % 15 == 0) {
			drawCursor = 1;
		}

		buttons = READ_BUTTONS() & 63;
		if (buttons != prevButtons) {
			buttonCounter = 0;
			if (buttons != 63) {
				// Process Down
				if (cursorVis) {
					DRAW_SPRITE((byte*)_cursor, x, y, 32, 32, 0, DRAW_XOR);
					cursorVis = 0;
				}
				drawCursor = 1;
				processButtons(buttons);
			}
		} else if (buttons != 63) {
			++buttonCounter;
			if ((buttonCounter > 10) && (buttonCounter % 5) == 0) {
				if (cursorVis) {
					DRAW_SPRITE((byte*)_cursor, x, y, 32, 32, 0, DRAW_XOR);
					cursorVis = 0;
				}
				drawCursor = 1;
				processButtons(buttons);
			}
		}
		prevButtons = buttons;
		
		WAIT();		
	}

	return 0;
}