static byte const _cursor[32] = { 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF, 0xFF };

byte* _pieces[32];
byte _map[8][4];
byte _cur_x = 0;
byte _cur_y = 0;

void init_pieces() {
	byte i = 0;
	for (; i < 32; ++i) {
		_pieces[i] = (byte*)malloc(32);
	}
}

void load_puzzle(byte* puzzle) {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte i = 0;
	unsigned int o1 = 0;
	unsigned int o2 = 0;
	
	for (y = 0; y < 4; ++y) {
		for (x = 0; x < 8; ++x) {			
			byte* piece = _pieces[i];

			_map[x][y] = i;

			o1 = ((unsigned int)y * (unsigned int)256) + ((unsigned int)x * (unsigned int)16);
			o2 = o1 + (unsigned int)128;

			for (xb = 0; xb < 16; ++xb) {
				piece[xb] = puzzle[o1 + xb];
				piece[xb + 16] = puzzle[o2 + xb];
			}

			++i;			
		}
	}
}

void randomize() {
	byte x = 0;
	byte y = 0;
	byte hash[32];
	for (x = 0; x < 32; ++x) {
		hash[x] = 0;
	}

	for (y = 0; y < 4; ++y) {
		for (x = 0; x < 8; ++x) {
			while (1) {
				byte r = (byte)rand() % 32;
				if (hash[r] == 0) {
					_map[x][y] = r;
					hash[r] = 1;
					break;
				}
			}
		}
	}
}

void draw_complete() {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte yb = 0;
	
	for (yb = 0; yb < 4; ++yb) {
		x = 0;
		for (xb = 0; xb < 8; ++xb) {
			i = _map[xb][yb];
			if (i == 0) {
				CLEAR_SPRITE(x, y, 16, 16);
			} else {
				DRAW_SPRITE(_pieces[i], x, y, 16, 16, 0, DRAW_NOP);	
			}			
			x += 16;
		}

		y += 16;
	}
}

int main() {
	byte x = 0;
	byte y = 0;
	byte xb = 0;
	byte yb = 0;

	api_init();

	init_pieces();
	load_puzzle((byte*)_image);
	randomize();
	draw_complete();
	
	for (;;) {			
		x = _cur_x * 16;
		y = _cur_y * 16;

		DRAW_SPRITE((byte*)_cursor, x, y, 16, 16, 0, DRAW_NOP);

		DISPLAY();
		
		WAIT();		
	}

	return 0;
}